import React, { useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

const AddUser = () => {
  const navigate = useNavigate(); // Initialize useNavigate
  const [addUser, setAddUser] = useState({
    nama_user: '',
    role: '',
    username: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAddUser((prev) => ({ ...prev, [name]: value }));
  };

  const handleClick = async (event) => {
    event.preventDefault();
    setLoading(true);

    const token = sessionStorage.getItem("Token"); // Ambil token dari sessionStorage

    try {
      const response = await axios.post("http://localhost:8080/user", addUser, {
        headers: {
          Authorization: `Bearer ${token}`, // Kirim token dalam header
        },
      });

      if (response.data) {
        toast.success("User berhasil ditambahkan!");
        navigate("/admin/user"); // Kembali ke daftar pengguna setelah berhasil
      } else {
        throw new Error("Data tidak ditemukan dari server");
      }
    } catch (error) {
      console.error("Add user error:", error);
      toast.error("Terjadi kesalahan saat menambah user. Silakan coba lagi.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="mt-8 mx-16">
      <form onSubmit={handleClick}>
        {/* Input fields for user details */}
        <div>
          <label htmlFor="nama_user">Nama User</label>
          <input
            type="text"
            name="nama_user"
            value={addUser.nama_user}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="role">Role</label>
          <select
            name="role"
            value={addUser.role}
            onChange={handleChange}
            required
          >
            <option value="">Pilih Role</option>
            <option value="kasir">Kasir</option>
            <option value="admin">Admin</option>
            <option value="manajer">Manager</option>
          </select>
        </div>
        <div>
          <label htmlFor="username">Username</label>
          <input
            type="text"
            name="username"
            value={addUser.username}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            name="password"
            value={addUser.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? "Loading..." : "Simpan"}
        </button>
      </form>
    </div>
  );
};

export default AddUser;
