import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export default function Pemesanan() {
  const [menu, setMenu] = useState([]);
  const [meja, setMeja] = useState([]);
  const [pesanan, setPesanan] = useState([]);
  const [selectedMeja, setSelectedMeja] = useState(null);
  const [showModal, setShowModal] = useState(false);
  
  useEffect(() => {
    fetchMenu();
    fetchMeja();
  }, []);

  const fetchMenu = async () => {
    try {
      const response = await axios.get("http://localhost:8080/api/menu");
      setMenu(response.data);
    } catch (error) {
      console.error("Error fetching menu:", error);
    }
  };

  const fetchMeja = async () => {
    try {
      const response = await axios.get("http://localhost:8080/api/meja");
      setMeja(response.data);
    } catch (error) {
      console.error("Error fetching meja:", error);
    }
  };

  const handleSelectChange = (event) => {
    setSelectedMeja(event.target.value);
  };

  const handleIncreaseClick = (id_menu) => {
    const updatedMenu = menu.map((item) => {
      if (item.id_menu === id_menu) {
        return { ...item, qty: item.qty + 1 };
      }
      return item;
    });
    setMenu(updatedMenu);
  };

  const handleDecreaseClick = (id_menu) => {
    const updatedMenu = menu.map((item) => {
      if (item.id_menu === id_menu && item.qty > 0) {
        return { ...item, qty: item.qty - 1 };
      }
      return item;
    });
    setMenu(updatedMenu);
  };

  const checkPemesanan = () => {
    const pesananBaru = menu.filter((item) => item.qty > 0);
    if (pesananBaru.length === 0) {
      toast.error("Silakan pilih menu terlebih dahulu!");
      return;
    }
    setPesanan(pesananBaru);
    setShowModal(true);
  };

  return (
    <div>
      <div className="fixed top-0 w-full pointer-events-none">
        <select
          onChange={handleSelectChange}
          className="pointer-events-auto text-sm rounded-lg block p-2.5 bg-[#4A6145] my-24 mx-8 float-right py-3 placeholder-gray-400 text-white focus:ring-sky-900 focus:border-sky-900"
        >
          <option value="">Pilih meja pelanggan</option>
          {meja.map((option) => (
            <option
              key={option.id_meja}
              value={option.nomor_meja}
              disabled={option.status === "tidak_tersedia"}
            >
              Meja nomor {option.nomor_meja} {option.status === "tidak_tersedia" ? "(Tidak Tersedia)" : ""}
            </option>
          ))}
        </select>
      </div>
      <div>
        <h6 className="mt-6 ml-5 text-4xl font-sans font-semibold">Makanan</h6>
        <div className="flex flex-wrap gap-5 mt-5 ml-5">
          {menu.map((menu) => {
            if (menu.jenis === "makanan") {
              return (
                <div
                  key={menu.id}
                  className="max-w-sm bg-white border border-gray-200 rounded-lg shadow"
                >
                  <img
                    className="w-80 h-60 p-8 rounded-t-lg"
                    src={`http://localhost:8080/gambar/menu/${menu.gambar}`}
                    alt="product"
                  />
                  <div className="px-5 pb-5">
                    <h5 className="text-xl font-semibold tracking-tight text-gray-900">
                      {menu.nama_menu}
                    </h5>
                    <div className="mt-2 flex items-center justify-between">
                      <span className="text-3xl font-bold text-gray-900">
                        Rp{menu.harga.toLocaleString()}
                      </span>
                      <div className="inline-flex rounded-md" role="group">
                        <button
                          onClick={() => handleDecreaseClick(menu.id_menu)}
                          type="button"
                          className="px-4 py-2 text-sm font-medium text-white bg-[#B30000] border border-gray-200 rounded-l-lg hover:bg-red-600 focus:z-10 focus:ring-2"
                        >
                          -
                        </button>
                        <input
                          type="text"
                          className="text-center py-2 text-sm font-medium text-gray-900 w-14 bg-white border-t border-b border-gray-200 hover:bg-gray-100 focus:z-10 focus:ring-2"
                          value={menu.qty}
                          disabled
                        />
                        <button
                          onClick={() => handleIncreaseClick(menu.id_menu)}
                          type="button"
                          className="px-4 py-2 text-sm font-medium text-white bg-[#B30000] border border-gray-200 rounded-r-md hover:bg-red-600 focus:z-10 focus:ring-2"
                        >
                          +
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              );
            }
            return null;
          })}
        </div>
        <h6 className="mt-6 ml-5 text-4xl font-sans font-semibold">Minuman</h6>
        <div className="flex flex-wrap gap-5 mt-5 ml-5">
          {menu.map((menu) => {
            if (menu.jenis === "minuman") {
              return (
                <div
                  key={menu.id}
                  className="max-w-sm bg-white border border-gray-200 rounded-lg shadow"
                >
                  <img
                    className="w-80 h-60 p-8 rounded-t-lg"
                    src={`http://localhost:8080/gambar/menu/${menu.gambar}`}
                    alt="product"
                  />
                  <div className="px-5 pb-5">
                    <h5 className="text-xl font-semibold tracking-tight text-gray-900">
                      {menu.nama_menu}
                    </h5>
                    <div className="mt-2 flex items-center justify-between">
                      <span className="text-3xl font-bold text-gray-900">
                        Rp{menu.harga.toLocaleString()}
                      </span>
                      <div className="inline-flex rounded-md" role="group">
                        <button
                          onClick={() => handleDecreaseClick(menu.id_menu)}
                          type="button"
                          className="px-4 py-2 text-sm font-medium text-white bg-[#B30000] border border-gray-200 rounded-l-lg hover:bg-red-600 focus:z-10 focus:ring-2"
                        >
                          -
                        </button>
                        <input
                          type="text"
                          className="text-center py-2 text-sm font-medium text-gray-900 w-14 bg-white border-t border-b border-gray-200 hover:bg-gray-100 focus:z-10 focus:ring-2"
                          value={menu.qty}
                          disabled
                        />
                        <button
                          onClick={() => handleIncreaseClick(menu.id_menu)}
                          type="button"
                          className="px-4 py-2 text-sm font-medium text-white bg-[#B30000] border border-gray-200 rounded-r-md hover:bg-red-600 focus:z-10 focus:ring-2"
                        >
                          +
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              );
            }
            return null;
          })}
        </div>
      </div>
      <div className="fixed bottom-0 w-full pointer-events-none">
        <button
          onClick={checkPemesanan}
          className="pointer-events-auto bottom-0 my-8 mx-8 float-right inline-block px-10 py-3 rounded-lg bg-[#B30000] text-white hover:bg-red-600"
        >
          Pesan
        </button>
      </div>

      {showModal ? (
        <div>
          <div className="justify-center items-center flex overflow-x-hidden overflow-y-auto fixed inset-0 z-50 outline-none focus:outline-none">
            <div className="relative w-auto my-6 mx-auto max-w-3xl max-h-screen">
              <div className="flex h-full flex-col overflow-y-auto bg-white shadow-xl">
                <div className="flex-1 overflow-y-auto py-6 px-4 sm:px-6">
                  <div className="flex items-start justify-between">
                    <p className="text-lg font-medium text-gray-900">
                      Pesanan Meja nomor {selectedMeja}
                    </p>
                    <button
                      onClick={() => setShowModal(false)}
                      type="button"
                      className="absolute top-3 right-2.5 text-gray-400 bg-transparent rounded-lg text-sm p-1.5 ml-auto inline-flex items-center"
                      data-modal-hide="authentication-modal"
                    >
                      <svg
                        aria-hidden="true"
                        className="w-5 h-5"
                        fill="currentColor"
                        viewBox="0 0 20 20"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          fillRule="evenodd"
                          d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z"
                          clipRule="evenodd"
                        ></path>
                      </svg>
                      <span className="sr-only">Close modal</span>
                    </button>
                  </div>

                  <div className="mt-8">
                    <div className="flow-root">
                      <ul
                        role="list"
                        className="-my-6 divide-y divide-gray-200"
                      >
                        {pesanan.map((product) => (
                          <li key={product.id_menu} className="flex py-6">
                            <div className="h-24 w-24 flex-shrink-0 overflow-hidden rounded-md border border-gray-200">
                              <img
                                className="h-full w-full object-cover object-center"
                                src={`http://localhost:8080/gambar/menu/${product.gambar}`}
                                alt={product.nama_menu}
                              />
                            </div>
                            <div className="ml-4 flex flex-1 flex-col">
                              <div>
                                <div className="flex justify-between text-base font-medium text-gray-900">
                                  <h3>
                                    <span>{product.nama_menu}</span>
                                  </h3>
                                  <p className="text-lg font-semibold">
                                    Rp{(product.harga * product.qty).toLocaleString()}
                                  </p>
                                </div>
                                <p className="mt-1 text-sm text-gray-500">
                                  Jumlah: {product.qty}
                                </p>
                              </div>
                            </div>
                          </li>
                        ))}
                      </ul>
                    </div>
                  </div>
                  <div className="flex justify-end border-t border-gray-200">
                    <button
                      onClick={() => {
                        // Logika pemesanan selesai di sini
                        toast.success("Pemesanan berhasil!");
                        setShowModal(false);
                      }}
                      className="mt-4 w-full inline-flex justify-center rounded-md border border-transparent bg-[#B30000] py-2 px-4 text-sm font-medium text-white shadow-sm hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2"
                    >
                      Konfirmasi Pesanan
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      ) : null}
      <ToastContainer />
    </div>
  );
}
