import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import classNames from 'classnames';

const TABS_LINK = [
  { key: 'pemesanan', label: 'Pemesanan', path: '/kasir/pemesanan' },
  { key: 'riwayat', label: 'Riwayat', path: '/kasir/riwayat' },
];

export default function SidebarKasir() {
  const navigate = useNavigate();
  const { pathname } = useLocation();

  const handleLogout = () => {
    sessionStorage.clear();
    navigate('/');
    window.location.reload();
  };

  return (
    <aside className="w-64 bg-[#B30000] text-white sticky top-0 h-screen">
      <div className="px-4 py-6">
        <h2 className="text-center text-lg font-bold mb-6">Wikusama Cafe</h2>
        <ul className="space-y-4">
          {TABS_LINK.map((item) => (
            <li key={item.key}>
              <Link
                to={item.path}
                className={classNames(
                  'flex items-center p-3 rounded-lg transition',
                  pathname === item.path ? 'bg-[#8B0000]' : 'hover:bg-[#700000]'
                )}
              >
                <span className="ml-3">{item.label}</span>
              </Link>
            </li>
          ))}
          <li>
            <button
              onClick={handleLogout}
              className="w-full flex items-center p-3 rounded-lg bg-[#8B0000] hover:bg-[#700000] transition"
            >
              <svg
                aria-hidden="true"
                className="w-6 h-6 text-white"
                fill="currentColor"
                viewBox="0 0 20 20"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  clipRule="evenodd"
                  fillRule="evenodd"
                  d="M3 3a1 1 0 00-1 1v12a1 1 0 102 0V4a1 1 0 00-1-1zm10.293 9.293a1 1 0 001.414 1.414l3-3a1 1 0 000-1.414l-3-3a1 1 0 10-1.414 1.414L14.586 9H7a1 1 0 100 2h7.586l-1.293 1.293z"
                ></path>
              </svg>
              <span className="ml-3">Logout</span>
            </button>
          </li>
        </ul>
      </div>
    </aside>
  );
}
