import React from "react"; 
import classNames from 'classnames';
import { Link, useLocation } from 'react-router-dom';
import { useNavigate } from "react-router-dom";

const TABS_LINK = [
    {
        key: 'pemesanan',
        label: 'Pemesanan',
        path: '/kasir/pemesanan',
        icon: (
            <svg
                aria-hidden="true"
                className="flex-shrink-0 w-6 h-6 text-white"
                fill="currentColor"
                viewBox="0 0 20 20"
                xmlns="http://www.w3.org/2000/svg"
            >
                <path d="M3 2a1 1 0 011-1h12a1 1 0 011 1v16a1 1 0 01-1 1H4a1 1 0 01-1-1V2zM4 0h12a2 2 0 012 2v16a2 2 0 01-2 2H4a2 2 0 01-2-2V2a2 2 0 012-2z" />
            </svg>
        ),
    },
    {
        key: 'riwayat',
        label: 'Riwayat',
        path: '/kasir/riwayat',
        icon: (
            <svg
                aria-hidden="true"
                className="flex-shrink-0 w-6 h-6 text-white"
                fill="currentColor"
                viewBox="0 0 20 20"
                xmlns="http://www.w3.org/2000/svg"
            >
                <path d="M2 10a8 8 0 1115.291 4.293A5.007 5.007 0 0014 11a5 5 0 00-5 5c0 1.38.564 2.635 1.471 3.535A7.978 7.978 0 012 10z" />
            </svg>
        ),
    },
];

export default function TabsKasir() {
    const navigate = useNavigate();
    const { pathname } = useLocation();

    const handleLogout = () => {
        sessionStorage.clear();
        navigate('/');
        window.location.reload();
    };

    return (
        <div className="flex min-h-screen">
            {/* Sidebar */}
            <aside className="w-64 bg-[#B30000] text-white sticky top-0 h-screen">
                <div className="h-full px-3 py-4 overflow-y-auto bg-gray-50 dark:bg-[#B30000]">
                    <h2 className="text-white text-center mb-4 text-lg font-bold">Wikusama Cafe</h2>
                    <ul className="space-y-2">
                        {TABS_LINK.map((item) => (
                            <li key={item.key}>
                                <Link
                                    to={item.path}
                                    className={classNames(
                                        'flex items-center p-2 text-base font-normal text-white rounded-lg transition duration-75',
                                        pathname === item.path
                                            ? 'bg-[#8B0000]' // Active tab color
                                            : 'hover:bg-[#700000]' // Hover effect for inactive tabs
                                    )}
                                >
                                    {item.icon}
                                    <span className="flex-1 ml-3 whitespace-nowrap">{item.label}</span>
                                </Link>
                            </li>
                        ))}
                        <li>
                            <button
                                onClick={handleLogout}
                                className="flex items-center p-2 text-base font-normal text-white rounded-lg hover:bg-[#700000] transition duration-75 w-full"
                            >
                                <svg
                                    aria-hidden="true"
                                    className="flex-shrink-0 w-6 h-6 text-white"
                                    fill="currentColor"
                                    viewBox="0 0 20 20"
                                    xmlns="http://www.w3.org/2000/svg"
                                >
                                    <path
                                        clipRule="evenodd"
                                        fillRule="evenodd"
                                        d="M3 3a1 1 0 00-1 1v12a1 1 0 102 0V4a1 1 0 00-1-1zm10.293 9.293a1 1 0 001.414 1.414l3-3a1 1 0 000-1.414l-3-3a1 1 0 10-1.414 1.414L14.586 9H7a1 1 0 100 2h7.586l-1.293 1.293z"
                                    ></path>
                                </svg>
                                <span className="ml-3">Logout</span>
                            </button>
                        </li>
                    </ul>
                </div>
            </aside>
        </div>
    );
}
