import React from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import classNames from "classnames";

const TABS_LINK = [
  {
    key: "pemesanan",
    label: "Pemesanan",
    path: "/kasir/pemesanan",
  },
  {
    key: "riwayat",
    label: "Riwayat",
    path: "/kasir/riwayat",
  },
];

export default function SidebarKasir() {
  const navigate = useNavigate();

  const handleLogout = () => {
    sessionStorage.clear();
    navigate("/");
    window.location.reload();
  };

  return (
    <div className="flex min-h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-[#B30000] text-white">
        <div className="h-full p-4">
          <h2 className="text-center text-lg font-bold mb-6">Wikusama Cafe</h2>
          <ul className="space-y-2">
            {TABS_LINK.map((item) => (
              <SidebarLink key={item.key} item={item} />
            ))}
            <li>
              <button
                onClick={handleLogout}
                className="flex items-center p-2 w-full text-white rounded-lg hover:bg-[#8B0000]"
              >
                <svg
                  aria-hidden="true"
                  className="w-6 h-6"
                  fill="currentColor"
                  viewBox="0 0 20 20"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    clipRule="evenodd"
                    fillRule="evenodd"
                    d="M3 3a1 1 0 00-1 1v12a1 1 0 102 0V4a1 1 0 00-1-1zm10.293 9.293a1 1 0 001.414 1.414l3-3a1 1 0 000-1.414l-3-3a1 1 0 10-1.414 1.414L14.586 9H7a1 1 0 100 2h7.586l-1.293 1.293z"
                  ></path>
                </svg>
                <span className="ml-3">Logout</span>
              </button>
            </li>
          </ul>
        </div>
      </aside>

      {/* Main Content */}
      <div className="flex-1 p-6">
        <h1 className="text-2xl font-bold">Konten Kasir</h1>
        <p>Isi konten seperti pemesanan, riwayat, dan menu lainnya akan muncul di sini.</p>
      </div>
    </div>
  );
}

function SidebarLink({ item }) {
  const { pathname } = useLocation();

  return (
    <li>
      <Link
        to={item.path}
        className={classNames(
          pathname === item.path
            ? "bg-[#8B0000] text-white"
            : "hover:bg-[#700000]",
          "flex items-center p-2 text-white rounded-lg"
        )}
      >
        <span className="ml-3">{item.label}</span>
      </Link>
    </li>
  );
}
