import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export default function Pemesanan() {
  const headers = {
    Authorization: `Bearer ${sessionStorage.getItem("Token") || ""}`, // Pastikan token tidak null
  };

  const navigate = useNavigate();
  const [menu, setMenu] = useState([]);
  const [meja, setMeja] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [pesanan, setPesanan] = useState([]);
  const [id_menu, setIdMenu] = useState("");
  const [selectedMeja, setSelectedMeja] = useState("");
  const [namaPelanggan, setNamaPelanggan] = useState("");
  const [detail_transaksi, setDetailTransaksi] = useState([]);

  const totalPrice = pesanan.reduce((total, item) => total + item.harga * item.qty, 0);

  const selectedOption = meja.find((option) => option.nomor_meja === selectedMeja);

  // Fungsi untuk mendapatkan data menu & meja
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/menu", { headers });
        console.log("Menu Response:", response.data); // Cek apakah datanya benar

        // Pastikan struktur data benar
        const menuData = response.data?.menu || [];
        const menuWithQty = menuData.map((item) => ({ ...item, qty: 0 }));
        setMenu(menuWithQty);

        const mejaResponse = await axios.get("http://localhost:8080/meja", { headers });
        console.log("Meja Response:", mejaResponse.data);
        setMeja(mejaResponse.data?.meja || []);
      } catch (error) {
        console.error("Error fetching data:", error);
        toast.error("Gagal memuat data. Pastikan server aktif.");
      }
    };

    fetchData();
  }, []);

  const handleIncreaseClick = (id_menu) => {
    setMenu((prevMenu) =>
      prevMenu.map((item) => 
        item.id_menu === id_menu ? { ...item, qty: item.qty + 1 } : item
      )
    );
  };

  const handleDecreaseClick = (id_menu) => {
    setMenu((prevMenu) =>
      prevMenu.map((item) => 
        item.id_menu === id_menu && item.qty > 0 ? { ...item, qty: item.qty - 1 } : item
      )
    );
  };

  const checkPemesanan = () => {
    const pesanan = menu.filter((item) => item.qty > 0);
    setPesanan(pesanan);

    setDetailTransaksi(
      pesanan.map((item) => ({
        id_menu: item.id_menu,
        qty: item.qty,
        subtotal: item.harga * item.qty,
      }))
    );

    if (!selectedOption) {
      toast.info("Pilih nomor meja");
    } else if (selectedOption.status === "tidak_tersedia") {
      toast.error("Meja yang dipilih tidak tersedia");
    } else if (pesanan.length === 0) {
      toast.info("Pesanan kosong");
    } else {
      setShowModal(true);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data_transaksi = {
      id_user: sessionStorage.getItem("id_user"),
      id_meja: selectedOption.id_meja,
      nama_pelanggan: namaPelanggan,
      listMenu: detail_transaksi.map((item) => ({
        id_menu: item.id_menu,
        qty: item.qty,
      })),
    };

    try {
      const response = await axios.post(
        "http://localhost:8080/transaksi",
        data_transaksi,
        { headers }
      );

      if (response.status === 200) {
        await axios.put(
          `http://localhost:8080/meja/${selectedOption.id_meja}`,
          { status: "tidak_tersedia" },
          { headers }
        );
        navigate("/kasir/riwayat");
      }
    } catch (error) {
      const errorMsg =
        error.response?.data?.message || "Gagal memproses transaksi";
      toast.error(`Error: ${errorMsg}`);
      console.error("Error:", error);
    }
  };

  return (
    <div>
      {/* Render Meja Select */}
      <select onChange={(e) => setSelectedMeja(e.target.value)}>
        <option value="">Pilih meja pelanggan</option>
        {meja.map((option) => (
          <option
            key={option.id_meja}
            value={option.nomor_meja}
            disabled={option.status === "tidak_tersedia"}
          >
            Meja nomor {option.nomor_meja} {option.status === "tidak_tersedia" ? "(Tidak Tersedia)" : ""}
          </option>
        ))}
      </select>

      {/* Render Menu */}
      {menu.map((item) => (
        <div key={item.id_menu}>
          <h3>{item.nama_menu}</h3>
          <p>Harga: Rp {item.harga.toLocaleString()}</p>
          <button onClick={() => handleDecreaseClick(item.id_menu)}>-</button>
          <input type="text" value={item.qty} readOnly />
          <button onClick={() => handleIncreaseClick(item.id_menu)}>+</button>
        </div>
      ))}

      <button onClick={checkPemesanan}>Pesan</button>
      <ToastContainer />
    </div>
  );
}
