import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export default function Pemesanan() {
  const navigate = useNavigate();

  const headers = {
    Authorization: `Bearer ${sessionStorage.getItem("Token")}`,
  };

  const [menu, setMenu] = useState([]);
  const [meja, setMeja] = useState([]);
  const [selectedMeja, setSelectedMeja] = useState("");
  const [pesanan, setPesanan] = useState([]);
  const [namaPelanggan, setNamaPelanggan] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [detailTransaksi, setDetailTransaksi] = useState([]);

  const totalPrice = pesanan.reduce(
    (total, item) => total + item.harga * item.qty,
    0
  );

  // Fetch data menu dan meja
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [menuRes, mejaRes] = await Promise.all([
          axios.get("http://localhost:8080/menu/", { headers }),
          axios.get("http://localhost:8080/meja/", { headers }),
        ]);
        setMenu(menuRes.data.menu.map((item) => ({ ...item, qty: 0 })));
        setMeja(mejaRes.data.meja);
      } catch (error) {
        console.error("Error fetching data:", error);
        toast.error("Gagal memuat data menu atau meja.");
      }
    };
    fetchData();
  }, []);

  const handleIncreaseClick = (id_menu) => {
    setMenu((prevMenu) =>
      prevMenu.map((item) =>
        item.id_menu === id_menu ? { ...item, qty: item.qty + 1 } : item
      )
    );
  };

  const handleDecreaseClick = (id_menu) => {
    setMenu((prevMenu) =>
      prevMenu.map((item) =>
        item.id_menu === id_menu && item.qty > 0
          ? { ...item, qty: item.qty - 1 }
          : item
      )
    );
  };

  const handleSelectChange = (e) => {
    setSelectedMeja(e.target.value);
  };

  const checkPemesanan = () => {
    const pesananTerpilih = menu.filter((item) => item.qty > 0);
    if (pesananTerpilih.length === 0) {
      toast.info("Pesanan kosong.");
      return;
    }

    const selected = meja.find((m) => m.nomor_meja === selectedMeja);
    if (!selected) {
      toast.info("Pilih nomor meja.");
      return;
    }
    if (selected.status === "tidak_tersedia") {
      toast.error("Meja tidak tersedia.");
      return;
    }

    setPesanan(pesananTerpilih);
    setDetailTransaksi(
      pesananTerpilih.map((item) => ({
        id_menu: item.id_menu,
        qty: item.qty,
        subtotal: item.harga * item.qty,
      }))
    );
    setShowModal(true);
  };

  const handleNamaPelanggan = (e) => setNamaPelanggan(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const selected = meja.find((m) => m.nomor_meja === selectedMeja);

    const dataTransaksi = {
      id_user: sessionStorage.getItem("id_user"),
      id_meja: selected.id_meja,
      nama_pelanggan: namaPelanggan,
      listMenu: detailTransaksi.map(({ id_menu, qty }) => ({ id_menu, qty })),
    };

    try {
      const response = await axios.post(
        "http://localhost:8080/transaksi",
        dataTransaksi,
        { headers }
      );

      if (response.status === 200) {
        await axios.put(
          `http://localhost:8080/meja/${selected.id_meja}`,
          { status: "tidak_tersedia" },
          { headers }
        );
        navigate("/kasir/riwayat");
      }
    } catch (error) {
      console.error("Error:", error);
      toast.error("Gagal memproses transaksi.");
    }
  };

  const removePesanan = (id_menu) => {
    setPesanan((prevPesanan) =>
      prevPesanan.filter((item) => item.id_menu !== id_menu)
    );
    setMenu((prevMenu) =>
      prevMenu.map((item) =>
        item.id_menu === id_menu ? { ...item, qty: 0 } : item
      )
    );
  };

  return (
    <div>
      <div className="fixed top-0 w-full">
        <select
          onChange={handleSelectChange}
          className="text-sm rounded-lg block p-2.5 bg-[#4A6145] text-white my-4 mx-8"
        >
          <option value="">Pilih meja pelanggan</option>
          {meja.map((m) => (
            <option
              key={m.id_meja}
              value={m.nomor_meja}
              disabled={m.status === "tidak_tersedia"}
            >
              Meja nomor {m.nomor_meja}{" "}
              {m.status === "tidak_tersedia" ? "(Tidak Tersedia)" : ""}
            </option>
          ))}
        </select>
      </div>

      <div className="flex flex-wrap gap-5 mt-5">
        {menu.map((item) => (
          <div key={item.id_menu} className="border p-4 rounded">
            <img
              src={`http://localhost:8080/gambar/menu/${item.gambar}`}
              alt={item.nama_menu}
              className="w-40 h-40"
            />
            <h4>{item.nama_menu}</h4>
            <p>Rp{item.harga.toLocaleString()}</p>
            <div className="flex">
              <button onClick={() => handleDecreaseClick(item.id_menu)}>-</button>
              <input type="text" value={item.qty} readOnly />
              <button onClick={() => handleIncreaseClick(item.id_menu)}>+</button>
            </div>
          </div>
        ))}
      </div>

      <button onClick={checkPemesanan} className="bg-green-500 text-white p-2">
        Pesan
      </button>

      {showModal && (
        <div>
          <h2>Pesanan Meja {selectedMeja}</h2>
          <ul>
            {pesanan.map((item) => (
              <li key={item.id_menu}>
                {item.nama_menu} - Qty: {item.qty}
                <button onClick={() => removePesanan(item.id_menu)}>Hapus</button>
              </li>
            ))}
          </ul>
          <p>Total: Rp{totalPrice.toLocaleString()}</p>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Nama Pelanggan"
              value={namaPelanggan}
              onChange={handleNamaPelanggan}
              required
            />
            <button type="submit">Checkout</button>
          </form>
        </div>
      )}

      <ToastContainer />
    </div>
  );
}
