import React, { useEffect, useState } from "react";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Modal from 'react-modal'; // Pastikan untuk mengimpor Modal

// Setel elemen root untuk modal
Modal.setAppElement('#root');

export default function Meja() {
    const [meja, setMeja] = useState([]);
    const [showModalAdd, setShowModalAdd] = useState(false);
    const [showModalEdit, setShowModalEdit] = useState(false);
    const [showModalDelete, setShowModalDelete] = useState(false);
    const [addMeja, setAddMeja] = useState({ nomor_meja: '', status: 'tersedia' });
    const [editMeja, setEditMeja] = useState({ id: '', nomor_meja: '', status: '' });
    const [deleteId, setDeleteId] = useState(null);

    const token = sessionStorage.getItem("Token");

    useEffect(() => {
        fetchMeja();
    }, []);

    const fetchMeja = async () => {
        try {
            const response = await fetch('http://localhost:8080/meja', {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                const errorText = await response.text();
                throw new Error(`Network response was not ok: ${errorText}`);
            }

            const data = await response.json();
            setMeja(data.meja || []);
        } catch (error) {
            console.error('Error fetching meja:', error);
            toast.error("Failed to fetch meja.");
        }
    };

    const postMeja = async (e) => {
        e.preventDefault(); 

        try {
            const response = await fetch('http://localhost:8080/meja', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`, 
                    'Content-Type': 'application/json', 
                },
                body: JSON.stringify(addMeja), 
            });

            const result = await response.json(); 

            if (!response.ok || result.message !== "Data meja berhasil ditambahkan") {
                if (result.message === "Nomor meja sudah ada, tidak bisa ditambahkan") {
                    throw new Error("Nomor meja sudah ada. Silakan gunakan nomor lain.");
                }
                throw new Error(result.message || "Failed to add meja.");
            }

            // Menampilkan notifikasi sukses
            toast.success("Meja berhasil ditambahkan", {
                position: "top-right", 
                autoClose: 5000, 
                hideProgressBar: false, 
                closeOnClick: true, 
                pauseOnHover: true, 
                draggable: true, 
                progress: undefined, 
            });

            fetchMeja(); 
            setShowModalAdd(false); 
            setAddMeja({ nomor_meja: '', status: 'tersedia' }); 
        } catch (error) {
            console.error('Error adding meja:', error);
            toast.error(error.message || "Failed to add meja."); 
        }
    };

    const updateMeja = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`http://localhost:8080/meja/${editMeja.id}`, {
                method: 'PUT',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(editMeja),
            });

            const result = await response.json();

            if (!response.ok) {
                throw new Error(result.message || "Failed to update meja.");
            }

            fetchMeja(); 
            setShowModalEdit(false); 
            toast.success(result.message || "Meja updated successfully."); 
        } catch (error) {
            console.error('Error updating meja:', error);
            toast.error(error.message || "Failed to update meja."); 
        }
    };

    const selectIdDelete = (id_meja) => {
        setDeleteId(id_meja);
        setShowModalDelete(true);
    };

    const deleteMeja = async () => {
        try {
            const response = await fetch(`http://localhost:8080/meja/${deleteId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const result = await response.json();
                throw new Error(result.message || "Failed to delete meja.");
            }

            fetchMeja(); 
            setShowModalDelete(false); 
            toast.success("Meja deleted successfully.");
        } catch (error) {
            console.error('Error deleting meja:', error);
            toast.error(error.message || "Failed to delete meja.");
        }
    };

    return (
        <div>
            <h1>Meja</h1>
            <button onClick={() => setShowModalAdd(true)}>Tambah Meja</button>
            <Modal isOpen={showModalAdd} onRequestClose={() => setShowModalAdd(false)}>
                <h2>Tambah Meja</h2>
                <form onSubmit={postMeja}>
                    <label>Nomor Meja:</label>
                    <input type="text" value={addMeja.nomor_meja} onChange={(e) => setAddMeja({ ...addMeja, nomor_meja: e.target.value })} />
                    <br />
                    <label>Status:</label>
                    <select value={addMeja.status} onChange={(e) => setAddMeja({ ...addMeja, status: e.target.value })}>
                        <option value="tersedia">Tersedia</option>
                        <option value="tidak tersedia">Tidak Tersedia</option>
                    </select>
                    <br />
                    <button type="submit">Tambah Meja</button>
                </form>
            </Modal>

            <Modal isOpen={showModalEdit} onRequestClose={() => setShowModalEdit(false)}>
                <h2>Edit Meja</h2>
                <form onSubmit={updateMeja}>
                    <label>Nomor Meja:</label>
                    <input type="text" value={editMeja.nomor_meja} onChange={(e) => setEditMeja({ ...editMeja, nomor_meja: e.target.value })} />
                    <br />
                    <label>Status:</label>
                    <select value={editMeja.status} onChange={(e) => setEditMeja({ ...editMeja, status: e.target.value })}>
                        <option value="tersedia">Tersedia</option>
                        <option value="tidak tersedia">Tidak Tersedia</option>
                    </select>
                    <br />
                    <button type="submit">Edit Meja</button>
                </form>
            </Modal>

            <Modal isOpen={showModalDelete} onRequestClose={() => setShowModalDelete(false)}>
                <h2>Hapus Meja</h2>
                <p>Apakah Anda yakin ingin menghapus meja ini?</p>
                <button onClick={deleteMeja}>Hapus</button>
                <button onClick={() => setShowModalDelete(false)}>Batal</button>
            </Modal>

            <table>
                <thead>
                    <tr>
                        <th>Nomor Meja</th>
                        <th>Status</th>
                        <th>Aksi</th>
                    </tr>
                </thead>
                <tbody>
                    {meja.map((meja) => (
                        <tr key={meja.id}>
                            <td>{meja.nomor_meja}</td>
                            <td>{meja.status}</td>
                            <td>
                                <button onClick={() => {
                                    setEditMeja(meja);
                                    setShowModalEdit(true);
                                }}>Edit</button>
                                <button onClick={() => selectIdDelete(meja.id)}>Hapus</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <ToastContainer />
        </div>
    );
}
