import React, { useEffect, useState } from "react";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function Meja() {
    const [meja, setMeja] = useState([]);
    const [showModalAdd, setShowModalAdd] = useState(false);
    const [showModalEdit, setShowModalEdit] = useState(false);
    const [showModalDelete, setShowModalDelete] = useState(false);
    const [addMeja, setAddMeja] = useState({ nomor_meja: '', status: 'tersedia' });
    const [editMeja, setEditMeja] = useState({ id: '', nomor_meja: '', status: '' });
    const [deleteId, setDeleteId] = useState(null);

    const token = sessionStorage.getItem("Token");

    useEffect(() => {
        fetchMeja();
    }, []);

    const fetchMeja = async () => {
        try {
            const response = await fetch('http://localhost:8080/meja', {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                const errorText = await response.text();
                throw new Error(`Network response was not ok: ${errorText}`);
            }

            const data = await response.json();
            setMeja(data.meja || []);
        } catch (error) {
            console.error('Error fetching meja:', error);
            toast.error("Failed to fetch meja.");
        }
    };

    const postMeja = async (e) => {
        e.preventDefault(); 

        try {
            const response = await fetch('http://localhost:8080/meja', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`, 
                    'Content-Type': 'application/json', 
                },
                body: JSON.stringify(addMeja), 
            });

            const result = await response.json(); 

            if (!response.ok || result.message !== "Data meja berhasil ditambahkan") {
                if (result.message === "Nomor meja sudah ada, tidak bisa ditambahkan") {
                    throw new Error("Nomor meja sudah ada. Silakan gunakan nomor lain.");
                }
                throw new Error(result.message || "Failed to add meja.");
            }

            // Menampilkan notifikasi sukses
            toast.success("Meja berhasil ditambahkan", {
                position: "top-right", 
                autoClose: 5000, 
                hideProgressBar: false, 
                closeOnClick: true, 
                pauseOnHover: true, 
                draggable: true, 
                progress: undefined, 
            });

            fetchMeja(); 
            setShowModalAdd(false); 
            setAddMeja({ nomor_meja: '', status: 'tersedia' }); 
        } catch (error) {
            console.error('Error adding meja:', error);
            toast.error(error.message || "Failed to add meja."); 
        }
    };

    const updateMeja = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`http://localhost:8080/meja/${editMeja.id}`, {
                method: 'PUT',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(editMeja),
            });

            const result = await response.json();

            if (!response.ok) {
                throw new Error(result.message || "Failed to update meja.");
            }

            fetchMeja(); 
            setShowModalEdit(false); 
            toast.success(result.message || "Meja updated successfully."); 
        } catch (error) {
            console.error('Error updating meja:', error);
            toast.error(error.message || "Failed to update meja."); 
        }
    };

    const selectIdDelete = (id_meja) => {
        setDeleteId(id_meja);
        setShowModalDelete(true);
    };

    const deleteMeja = async () => {
        try {
            const response = await fetch(`http://localhost:8080/meja/${deleteId}`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const result = await response.json();
                throw new Error(result.message || "Failed to delete meja.");
            }

            fetchMeja();
            setShowModalDelete(false);
            toast.success(" Meja deleted successfully.");
        } catch (error) {
            console.error('Error deleting meja:', error);
            toast.error(error.message || "Failed to delete meja.");
        }
    };

    const handleChange_Add = (e) => {
        setAddMeja({ ...addMeja, [e.target.name]: e.target.value });
    };

    const handleChange_Edit = (e) => {
        setEditMeja({ ...editMeja, [e.target.name]: e.target.value });
    };

    return (
        <div>
            <ToastContainer />
            <h1 className="text-3xl font-bold mb-4">Meja</h1>
            <button 
                onClick={() => setShowModalAdd(true)} 
                className="bg-[#4A6145] hover:bg-[#678161] text-white font-bold py-2 px-4 rounded"
            >
                Tambah Meja
            </button>
            <table className="w-full mt-4">
                <thead>
                    <tr>
                        <th className="px-4 py-2">Nomor Meja</th>
                        <th className="px-4 py-2">Status</th>
                        <th className="px-4 py-2">Aksi</th>
                    </tr>
                </thead>
                <tbody>
                    {meja.map((meja) => (
                        <tr key={meja.id}>
                            <td className="px-4 py-2">{meja.nomor_meja}</td>
                            <td className="px-4 py-2">{meja.status}</td>
                            <td className="px-4 py-2">
                                <button 
                                    onClick={() => {
                                        setEditMeja(meja);
                                        setShowModalEdit(true);
                                    }} 
                                    className="bg-[#4A6145] hover:bg-[#678161] text-white font-bold py-2 px-4 rounded"
                                >
                                    Edit
                                </button>
                                <button 
                                    onClick={() => selectIdDelete(meja.id)} 
                                    className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
                                >
                                    Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Modal for Add Meja */}
            {showModalAdd && (
                <Modal>
                    <div className="p-6">
                        <h3 className="text-lg font-semibold text-gray-900">Tambah Meja</h3>
                        <form onSubmit={postMeja}>
                            <div>
                                <label className="text-sm font-medium text-gray-600">Nomor Meja</label>
                                <input 
                                    type="text" 
                                    name="nomor_meja" 
                                    value={addMeja.nomor_meja} 
                                    onChange={handleChange_Add} 
                                    required 
                                    className="mt-1 border rounded-md w-full p-2" 
                                />
                            </div>
                            <div className="mt-4">
                                <label className="text-sm font-medium text-gray-600">Status</label>
                                <select 
                                    name="status" 
                                    value={addMeja.status} 
                                    onChange={handleChange_Add} 
                                    className="mt-1 border rounded-md w-full p-2"
                                >
                                    <option value="tersedia">Tersedia</option>
                                    <option value="tidak tersedia">Tidak Tersedia</option>
                                </select>
                            </div>
                            <button 
                                type="submit" 
                                className="mt-4 text-white bg-[#4A6145] hover:bg-[#678161] focus:ring-4 focus:outline-none font-medium rounded-lg text-sm inline-flex items-center px-5 py-2.5 text-center"
                            >
                                Tambahkan
                            </button>
                            <button 
                                onClick={() => setShowModalAdd(false)} 
                                type="button" 
                                className="mt-2 focus:ring-4 focus:outline-none rounded-lg border text-sm font-medium px-5 py-2.5 focus:z-10 bg-gray-700 text-gray-300 border-gray-500 hover:text-white hover:bg-gray-600 focus:ring-gray-600"
                            >
                                Cancel
                            </button>
                        </form>
                    </div>
                </Modal>
            )}

            {/* Modal for Edit Meja */}
            {showModalEdit && (
                <Modal>
                    <div className="p-6">
                        <h3 className="text-lg font-semibold text-gray-900">Edit Meja</h3>
                        <form onSubmit={updateMeja}>
                            <div>
                                <label className="text-sm font-medium text-gray-600">Nomor Meja</label>
                                <input 
                                    type="text" 
                                    name="nomor_meja" 
                                    value={editMeja.nomor_meja} 
                                    onChange={handleChange_Edit} 
                                    required 
                                    className="mt-1 border rounded-md w-full p-2" 
                                />
                            </div>
                            <div className="mt-4">
                                <label className="text-sm font-medium text-gray-600">Status</label>
                                <select 
                                    name="status" 
                                    value={editMeja.status} 
                                    onChange={handleChange_Edit} 
                                    className="mt-1 border rounded-md w-full p-2"
                                >
                                    <option value="tersedia">Tersedia</option>
                                    <option value="tidak tersedia">Tidak Tersedia</option>
                                </select>
                            </div>
                            <button 
                                type="submit" 
                                className="mt-4 text-white bg-[#4A6145] hover:bg-[#678161] focus:ring-4 focus:outline-none font-medium rounded-lg text-sm inline-flex items-center px-5 py-2.5 text-center"
                            >
                                Simpan
                            </button>
                            <button 
                                onClick={() => setShowModalEdit(false)} 
                                type="button" 
                                className="mt-2 focus:ring-4 focus:outline-none rounded-lg border text-sm font-medium px-5 py-2.5 focus:z-10 bg-gray-700 text-gray-300 border-gray-500 hover:text-white hover:bg-gray-600 focus:ring-gray-600"
                            >
                                Cancel
                            </button>
                        </form>
                    </div>
                </Modal>
            )}

            {/* Modal for Delete Meja */}
            {showModalDelete && (
                <Modal>
                    <div className="p-6">
                        <h3 className="text-lg font-semibold text-gray-900">Hapus Meja</h3>
                        <p>Apakah Anda yakin ingin menghapus meja ini?</p>
                        <button 
                            onClick={deleteMeja} 
                            className="mt-4 text-white bg-red-500 hover:bg-red-700 focus:ring-4 focus:outline-none font-medium rounded-lg text-sm inline-flex items-center px-5 py-2.5 text-center"
                        >
                            Hapus
                        </button>
                        <button 
                            onClick={() => setShowModalDelete(false)} 
                            type="button" 
                            className="mt-2 focus:ring-4 focus:outline-none rounded-lg border text-sm font-medium px-5 py-2.5 focus:z-10 bg-gray-700 text-gray-300 border-gray-500 hover:text-white hover:bg-gray-600 focus:ring-gray-600"
                        >
                            Cancel
                        </button>
                    </div>
                </Modal>
            )}
        </div>
    );
}
