import React, { useEffect, useState } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export default function Riwayat() {
  const headers = {
    Authorization: `Bearer ${sessionStorage.getItem("Token")}`,
  };

  const [transaksi, setTransaksi] = useState([]);
  const [filteredTransaksi, setFilteredTransaksi] = useState([]);
  const [meja, setMeja] = useState([]);
  const [search, setSearch] = useState("");
  const [nota, setNota] = useState(null);

  // Fetch transaksi dan meja data ketika komponen di-mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [transaksiRes, mejaRes] = await Promise.all([
          axios.get("http://localhost:3000/transaksi/", { headers }),
          axios.get("http://localhost:3000/meja/", { headers }),
        ]);
        setTransaksi(transaksiRes.data.transaksi);
        setFilteredTransaksi(transaksiRes.data.transaksi);
        setMeja(mejaRes.data.meja);
      } catch (err) {
        console.error("Error fetching data:", err);
        toast.error("Gagal memuat data.");
      }
    };
    fetchData();
  }, []);

  // Update state saat input search berubah
  const handleChange = (e) => {
    const keyword = e.target.value;
    setSearch(keyword);
    const filtered = transaksi.filter((data) =>
      data.meja?.nomor_meja === keyword
    );
    setFilteredTransaksi(filtered);
  };

  // Fungsi untuk menangani pembayaran
  const handleBayar = async (id) => {
    try {
      const selectedTransaksi = transaksi.find(
        (select) => select.id_transaksi === id
      );
  
      const selectedMeja = meja.find(
        (select) => select.id === selectedTransaksi.meja.id
      );
  
      if (!selectedTransaksi || !selectedMeja) {
        toast.error("Data transaksi atau meja tidak ditemukan.");
        return;
      }
  
      const updatedStatusTransaksi = {
        status: "lunas",
      };
  
      const updatedStatusMeja = {
        nomor_meja: selectedMeja.nomor_meja,
        status: "tersedia",
      };
  
      // Request PUT untuk memperbarui status meja
      await axios.put(
        `http://localhost:8080/meja/${selectedMeja.id_meja}`,
        updatedStatusMeja,
        { headers }
      );
  
      // Request PUT untuk memperbarui status transaksi
      await axios.put(
        `http://localhost:8080/transaksi/updatePembayaran/${id}`,
        updatedStatusTransaksi,
        { headers }
      );
  
      // Menampilkan nota setelah transaksi berhasil
      setNota(selectedTransaksi);
      toast.success("Transaksi berhasil diperbarui!");
  
      // Refresh data tanpa reload halaman
      setTransaksi((prev) =>
        prev.map((trx) =>
          trx.id_transaksi === id ? { ...trx, status: "lunas" } : trx
        )
      );
    } catch (error) {
      console.error("Error updating transaction:", error);
      toast.error("Gagal memperbarui transaksi.");
    }
  };
  

  return (
    <div className="bg-white min-h-screen p-8">
      <input
        type="text"
        value={search}
        onChange={handleChange}
        placeholder="Search by nomor meja"
        className="w-32 py-2 pl-10 ml-10 mt-5 text-sm rounded-md sm:w-auto focus:outline-none text-black bg-gray-200"
      />

      <div className="overflow-hidden rounded-lg border border-gray-200 shadow-md m-5">
        <table className="w-full border-collapse text-left text-sm text-gray-500">
          <thead className="bg-[#B30000] text-white">
            <tr>
              <th className="px-6 py-4 font-medium">Nama Pelanggan</th>
              <th className="px-6 py-4 font-medium">Nomor Meja</th>
              <th className="px-6 py-4 font-medium">Total Harga</th>
              <th className="px-6 py-4 font-medium">Status</th>
              <th className="px-6 py-4 font-medium"></th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-100 border-t border-gray-100">
            {filteredTransaksi.map((t) => (
              <tr key={t.id_transaksi} className="hover:bg-gray-50">
                <td className="px-6 py-4">{t.nama_pelanggan}</td>
                <td className="px-6 py-4">
                  Meja nomor {t.meja?.nomor_meja}
                </td>
                <td className="px-6 py-4">Rp{t.total}</td>
                <td className="px-6 py-4">
                  {t.status === "belum_bayar" ? (
                    <div className="flex gap-2">
                      <span className="inline-flex items-center gap-1 rounded-full bg-blue-50 px-2 py-1 text-xs font-semibold text-blue-600">
                        Belum Lunas
                      </span>
                      <button
                        onClick={() => handleBayar(t.id_transaksi)}
                        className="inline-flex items-center gap-1 rounded-full bg-[#B30000] px-2 py-1 text-xs font-semibold text-white"
                      >
                        Bayar
                      </button>
                    </div>
                  ) : (
                    <div className="flex gap-2">
                      <span className="inline-flex items-center gap-1 rounded-full bg-green-50 px-2 py-1 text-xs font-semibold text-green-600">
                        Lunas
                      </span>
                      <button
                        onClick={() => setNota(t)}
                        className="inline-flex items-center gap-1 rounded-full bg-indigo-50 px-2 py-1 text-xs font-semibold text-blue-600"
                      >
                        See Nota
                      </button>
                    </div>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {nota && (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <div className="text-center mb-4">
              <h2 className="text-xl font-bold">Wikusama Cafe</h2>
              <p className="text-sm">Jl.Danau Kerinci IV, Sawojajar, Kota Malang</p>
              <p className="text-sm">Telp: (021) 12345678</p>
            </div>

            <div className="mb-4">
              <p className="text-sm font-semibold">
                Nama Pelanggan: {nota.nama_pelanggan}
              </p>
              <p className="text-sm">Nomor Meja: {nota.meja?.nomor_meja}</p>
              <p className="text-sm">
                Tanggal: {new Date(nota.tgl_transaksi).toLocaleDateString()}
              </p>
              <p className="text-sm">Kasir: {nota.user?.nama_user}</p>
            </div>

            <table className="w-full mb-4">
              <thead>
                <tr>
                  <th className="text-left border-b pb-2 text-sm">Pesanan</th>
                  <th className="text-center border-b pb-2 text-sm">Jumlah</th>
                  <th className="text-right border-b pb-2 text-sm">Subtotal</th>
                </tr>
              </thead>
              <tbody>
                {nota.detail_transaksi?.map((detail, index) => (
                  <tr key={index}>
                    <td className="text-left py-2 text-sm">
                      {detail.menu?.nama_menu}
                    </td>
                    <td className="text-center py-2 text-sm">{detail.qty}</td>
                    <td className="text-right py-2 text-sm">
                      Rp{detail.subtotal}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            <div className="border-t pt-4 text-right">
              <p className="font-semibold text-sm">Total: Rp{nota.total}</p>
            </div>

            <div className="text-center mt-6">
              <button
                onClick={() => setNota(null)}
                className="bg-[#B30000] text-white px-4 py-2 rounded"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}

      <ToastContainer />
    </div>
  );
}
